  const Avatar = () => {
    return (
      <div className="col avatar">
        <img
          src={avatarSvg}
          alt="hello it's me , libregd"
          className="profilo"
        />
        <br />
        <SVG />
      </div>
    );
  };
  // 这部分是关于文本的
  type TextProps = {
    title?: string;
    spicial?: string;
    texts: string[];
  };
  const ContentText = ({ title, texts, spicial }: TextProps) => {
    return (
      <div className="col text">
        <p className="title">
          {title} <span>{spicial}</span>
        </p>
        {texts.map((str: string) => {
          return <p className="text-line">{str}</p>;
        })}
      </div>
    );
  };

  // 把这部分抽出来作为一个传参数的组件？
  type PartProps = {
    title: string;
  };

  const PartTitle = ({ title }: PartProps) => {
    return <h1 className="part-title">{title}</h1>;
  };
